queries.query:
CALL {
     MATCH (org:Organization) WHERE org.onboardingStatus IS NULL OR NOT org.onboardingStatus IN ['NOT_APPLICABLE','NOT_STARTED','ON_TRACK','LATE','STUCK','DONE','SUCCESSFUL'] with org RETURN count(org) as cnt
} RETURN sum(cnt)

CALL {
     MATCH (op:RenewalOpportunity) WHERE NOT (op)--(:DeletedContract) WITH op OPTIONAL MATCH (op)-[:ACTIVE_RENEWAL]-(c:Contract) WITH op, c WHERE (c IS NULL OR NOT c.status IN ['OUT_OF_CONTRACT','DRAFT']) AND op.renewedAt < datetime()-duration({minutes: 30}) AND op.internalStage = 'OPEN' RETURN count(op) as cnt
     UNION ALL
     MATCH (op:RenewalOpportunity) WHERE op.renewalLikelihood IS NOT NULL AND NOT op.renewalLikelihood IN ['','ZERO','LOW','MEDIUM','HIGH'] RETURN count(op) as cnt
} RETURN sum(cnt)
